from hetdesrun.component.registration import register
from hetdesrun.datatypes import DataType

import pandas as pd

# ***** DO NOT EDIT LINES BELOW *****
# These lines may be overwritten if component details or inputs/outputs change.
@register(
    inputs={"left": DataType.Any, "right": DataType.Any},
    outputs={"result": DataType.Any},
    name="Smaller",
    description="Checks if left is smaller than right",
    category="Basic",
    id="9f33cbbc-3935-e9f3-a682-99579e96a4d0",
    revision_group_id="9f33cbbc-3935-e9f3-a682-99579e96a4d0",
    version_tag="1.0.0"
)
def main(*, left, right):
    # entrypoint function for this component
    # ***** DO NOT EDIT LINES ABOVE *****
    # write your function code here.

    try:
        return {"result": left < right}
    except ValueError:
        return {"result": left.lt(right)}
