}

from hdutils import parse_default_value  # noqa: E402, F401


def main(*, dataframe):
    # entrypoint function for this component
    # ***** DO NOT EDIT LINES ABOVE *****
    if "timestamp" in dataframe.columns:
        dataframe.set_index("timestamp", inplace=True)

    nrows = len(dataframe.index)
    ncols = len(dataframe.columns)

    df_dict = dataframe.to_dict(orient="split")

    multitsframe_dict = {}
    multitsframe_dict["timestamp"] = np.repeat(df_dict["index"], ncols)
    multitsframe_dict["metric"] = df_dict["columns"] * nrows
    multitsframe_dict["value"] = flatten_list_of_sublists(df_dict["data"])

    return {"multitsframe": pd.DataFrame(multitsframe_dict)}


